import * as React from 'react';
import {useState, useEffect} from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput, Button, List } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';

const Espanya = () => {
    const [respTiempo, setRespTiempo]=useState({
        nombre:null,
        continente:null,
        capital: null,
        moneda: null,
        superficie:null,
        idioma: null,
        habitantes:null,
        bandera:null,
    })

    const getData = async () => {
        fetch('https://restcountries.com/v3.1/name/spain').then(response => {
            if (response.ok) {
                return response.json();
            } throw new Error('La petición ha fallado');
        }, networkError => {
            console.log(networkError.message);
        }).then(jsonResponse => {
            setRespTiempo({
                nombre: jsonResponse[0].name.common,
                continente: jsonResponse[0].continents[0],
                capital: jsonResponse[0].capital[0],
                moneda:jsonResponse[0].currencies,
                superficie:jsonResponse[0].area,
                idioma:jsonResponse[0].languages,
                habitantes:jsonResponse[0].population,
                bandera:jsonResponse[0].flag
            })
            console.log(jsonResponse[0]);
        });
    }

    useEffect(() =>{ 
        getData(); 
    },[])

    return (
        <View>
            <Text style={styles.title}>España</Text>
            <List.Section>
      <List.Accordion
        title="Información sobre el país"
        left={props => <List.Icon {...props} icon="compass-outline" />}>
        <List.Item title="Nombre: "></List.Item>
        <List.Item title="Continente: " />
        <List.Item title="Capital: " />
      </List.Accordion>

      <List.Accordion
        title="Información del tiempo"
        left={props => <List.Icon {...props} icon="weather-lightning-rainy" />}>
        <List.Item title="Tiempo meteorológico: " />
        
      </List.Accordion>
    </List.Section>
        </View>

    )
}

const styles = StyleSheet.create({
    layout: {
        flex: 1,
        justifyContent: 'center',
        textAlign: 'top',
        padding: 8,
    },
    separator: {
        fontWeight: 'bold',
        
    },
    title: {
        margin: 24,
        fontSize: 20,
        textAlign: 'top',
        fontWeight: 'bold',
        textAlign: 'center',
    },
});
export default Espanya;
