import * as React from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { TextInput, Button, List } from 'react-native-paper';
import { useNavigation } from '@react-navigation/native';

const Espanya = () => {
    const [expanded, setExpanded] = React.useState(true);
    return (
        <View>
            <Text style={styles.title}>Espanya</Text>
            &nbsp;
            <View>
                <Text style={styles.separator}>Información sobre el país</Text>
                <Text>Nombre: xxxx</Text>
                <Text>Continente:</Text>
                <Text>Capital:</Text>
                &nbsp;
            </View>
            <Text style={styles.separator}>Información del tiempo</Text>
        </View>

    )
}

const styles = StyleSheet.create({
    layout: {
        flex: 1,
        justifyContent: 'center',
        textAlign: 'top',
        padding: 8,
    },
    separator: {
        fontWeight: 'bold',
        
    },
    title: {
        margin: 24,
        fontSize: 20,
        textAlign: 'top',
        fontWeight: 'bold',
        textAlign: 'center',
    },
});
export default Espanya;
